para.programlisting:
function yprime = f(t,y)
    yprime = t+y
end

function vdot=%SUN_vdp1(t, v)
    mu = 1;
    vdot = [v(2); mu*(1-v(1)^2)*v(2)-v(1)]
end

function Xd = f(t,X,alpha)
    Xd = alpha*[-X(2);X(1)];
end
function corr = proj(t,X,err)
    corr = X/norm(X) - X;
end

alpha = 1;
X0 = [1;0];
tspan = 0:5;

[t,X]=cvode(list(f,alpha), tspan, X0,jacobian=[0 -alpha, alpha 0]);
disp(X-[cos(t);sin(t)])
disp(sum(X.^2,1))

[t,X]=cvode(list(f,alpha), tspan, X0,jacobian=[0 -alpha, alpha 0],projection=proj);
disp(X-[cos(t);sin(t)])
disp(sum(X.^2,1))

para.latex:
\begin{array}{rcl}y^\prime & = & f(t,y,p), \quad y(t_0) =y_0(p),\\
s^\prime & = & \frac{\partial f}{\partial y}s+\frac{\partial f}{\partial p},\quad s(t_0) = \frac{\partial y_0(p)}{\partial p}
,
\end{array}

para.programlisting:
function vdot=vdp(t, y, μ)
    vdot = [y(2); μ*(1-y(1)^2)*y(2)-y(1)]
end
[t,y,info] = cvode(vdp, 0:0.1:10, [2;1], sensPar = 1);
plot(t,info.s)
legend("$\Large\partial y_"+["1","2"]+"/\partial μ$");

para.scilab:image:
function vdot=vdp(t, v, mu)
vdot = [v(2); mu*(1-v(1)^2)*v(2)-v(1)]
end
[t,y,info] = cvode(vdp, 0:0.1:10, [2;1], sensPar=1);
plot(t,info.s)
legend("$\Large\partial y_"+["1","2"]+"/\partial μ$");
gcf().axes_size(2)=300
